dict.string:
require( 'workflows.php' );
$w = new Workflows();
$query = "{query}";

$url = "http://static.aqicn.org/mapi/?lang=en&n=50&term=$query&jsoncallback=";
$data = $w->request( $url );
$pattern = '/^\((.*)\);/';
preg_match($pattern,$data,$match);
$data = json_decode( '{"aqi": '. $match[1] .'}' );

function grade($arg) {
	if($arg == '-' ) {
		$out = "No Data";
		$icon = 'icons/7.png';
	}
	elseif( $arg>=0 && $arg<=50 ) {
		$out = "Good";
		$icon = 'icons/1.png';
	}
	elseif($arg>=51 && $arg<=100 ) {
		$out = "Moderate";
		$icon = 'icons/2.png';
	}
	elseif($arg>=101 && $arg<=150 ) {
		$out = "Unhealthy for Sensitive Groups";
		$icon = 'icons/3.png';
	}
	elseif($arg>=151 && $arg<=200 ) {
		$out = "Unhealthy";
		$icon = 'icons/4.png';
	}
	elseif($arg>=201 && $arg<=300 ) {
		$out = "Very Unhealthy";
		$icon = 'icons/5.png';
	}
	elseif($arg>=300 && $arg<=500 ) {
		$out = "Hazardous";
		$icon = 'icons/6.png';
	}
	else {
		$out = "Beyond Index";
		$icon = 'icons/6.png';
	}
	return array($out, $icon);
}

foreach ($data->aqi as $aqi) {
	list($grade, $icon) = grade($aqi->aqi);
	$w->result( $aqi->id, $aqi->id, $aqi->aqi .', '. $grade, $aqi->name, $icon );
};
echo $w->toxml();
